import React, { useState, useEffect } from "react";
import './App.css';
import axios from "axios";

function App() {
 const [categoryList, setCategoryList] = useState([]);
 const id = 7;
 const name_id = "null1";
 const status_id = "null1";
 const status_updateid = "null2";


 useEffect(() => {
   axios.get("http://localhost:3001/user").then((response) => {
     setCategoryList(response.data);
   });
 }, [categoryList]);

 const handlecreate = () => {
  try {
    axios.post(`http://localhost:3001/user/create`, {name: name_id, status: status_id},)
  }catch(err){
    console.log(err);
  }
}

const handledelete = () => {//deleteだけaxiosの書き方が違う．
  try {
    const url = 'http://localhost:3001/user/delete'
    const payload = {
      id: '7'
    }
    axios.delete(url, {
      data: payload
    }) // 正常にAPIコールできる
  } catch(e) {
    console.error(e)
  }
}

const handleupdate = () => {
  try {
    axios.put(`http://localhost:3001/user/update`, {status: status_updateid, id : id},)
  }catch(err){
    console.log(err);
  }
}

  return (
    <div className="App">
     <ul>
       {categoryList.map((val, index) => {
         return <li key={index}>id→{val.id} | name→{val.name} | status→{val.status}</li>
       })}
     </ul>
     <button onClick={handlecreate}>create</button>
     <br/>
     <button onClick={handledelete}>delete</button>
     <br/>
     <button onClick={handleupdate}>update</button>
    </div>
  );
}

export default App;
