import React, { useState, useEffect } from "react";
import './App.css';
import axios from "axios";

function App() {
 const [categoryList, setCategoryList] = useState([]);
 const [id, setId] = useState(1);
 const [name_id, setName_id] = useState("");
 const [status_id, setStatus_id] = useState("");
 const [status_updateid, setStatus_updateid] = useState("");

const handleChangeCreate = () => {
  setName_id();
  setStatus_id();
}

 useEffect(() => {
   axios.get("http://localhost:3001/user").then((response) => {
     setCategoryList(response.data);
   });
 }, [categoryList]);

 const handlecreate = () => {
  try {
    axios.post(`http://localhost:3001/user/create`, {name: name_id, status: status_id},)
  }catch(err){
    console.log(err);
  }
}

const handledelete = () => {//deleteだけaxiosの書き方が違う．
  try {
    const url = 'http://localhost:3001/user/delete'
    const payload = {
      id: '7'
    }
    axios.delete(url, {
      data: payload
    }) // 正常にAPIコールできる
  } catch(e) {
    console.error(e)
  }
}

const handleupdate = () => {
  try {
    axios.put(`http://localhost:3001/user/update`, {status: status_updateid, id : id},)
  }catch(err){
    console.log(err);
  }
}

  return (
    <div className="App">
     <ul>
       {categoryList.map((val, index) => {
         return <li key={index}>id→{val.id} | name→{val.name} | status→{val.status}</li>
       })}
     </ul>
     <form>
     <input  value={name_id} placeholder={"作成したい名前を入れて"}/>
     <input  value={status_id} placeholder={"作成したい状態を入れて"}/>
     <button onClick={handlecreate}>create</button>
     </form>
     <br/>
     <input  value={status_id} placeholder={"更新したい状態を入れて"}/>
     <input  value={id} placeholder={"更新したいIDを入れて"}/>
     <button onClick={handleupdate}>update</button>
     <br/>
     <input  value={id} placeholder={"削除したいIDを入れて"}/>
     <button onClick={handledelete}>delete</button>
    </div>
  );
}

export default App;
