import React, { useState, useEffect } from "react";
import './App.css';
import axios from "axios";

function App() {
 const [categoryList, setCategoryList] = useState([]);
 const id = 9;
 const name_id = "null1";
 const status_id = "null1";
 const status_updateid = "null1";


 useEffect(() => {
   axios.get("http://localhost:3001/user").then((response) => {
     setCategoryList(response.data);
   });
 }, [categoryList]);

 const handlecreate = () => {
  try {
    axios.post(`http://localhost:3001/user/create`, {name: name_id, status: status_id},)
  }catch(err){
    console.log(err);
  }
}

const handledelete = () => {
  try {
    axios.delete(`http://localhost:3001/user/delete`, {id : id},)
  }catch(err){
    console.log(err);
  }
}

const handleupdate = () => {
  try {
    axios.delete(`http://localhost:3001/user/update`, {status: status_updateid, id : id},)
  }catch(err){
    console.log(err);
  }
}

  return (
    <div className="App">
     <ul>
       {categoryList.map((val, index) => {
         return <div key={index}>{val.id}:{val.name}:{val.status}</div>
       })}
     </ul>
     <button onClick={handlecreate}>create</button>
     <button onClick={handledelete}>delete</button>
     <button onClick={handleupdate}>update</button>
    </div>
  );
}

export default App;
